package Exercise2;import java.util.Arrays;import java.util.NoSuchElementException;public class ArrayBag<E> implements Bag<E> {    private E[] array;    private int index = 0;    public ArrayBag(int size) {        this.array = (E[]) new Object[size];    }    @Override    public int getCurrentSize() {        int currentSize = 0;        for (int i = 0; i < array.length; i++) {            if (array[i] != null) {                currentSize++;            }        }        return currentSize;    }    @Override    public boolean isFull() {        return getCurrentSize() == array.length;    }    @Override    public boolean isEmpty() {        for (int i = 0; i < array.length; i++) {            if (array[i] != null) {                return false;            }        }        return true;    }    @Override    public boolean add(E newEntry) {        if (isFull()) {            System.out.println("Array is full");            return false;        } else {            array[index] = newEntry;            index++;            return true;        }    }    @Override    public E remove() {        if (isEmpty()) {            throw new NoSuchElementException();        } else {            index--;            E removed = array[index];            array[index] = null;            return removed;        }    }    @Override    public boolean remove(E anEntry) {        if (isEmpty()) {            throw new NoSuchElementException();        } else {            for (int i = 0; i < array.length; i++) {                if (array[i] == anEntry) {                    array[i] = null;                    return true;                }            }            return false;        }    }    @Override    public void clear() {        Arrays.fill(array, null);    }    @Override    public int getFrequencyOf(E anEntry) {        int frequency = 0;        for (int i = 0; i < array.length; i++) {            if (array[i] == anEntry) {                frequency++;            }        }        return frequency;    }    @Override    public boolean contains(E anEntry) {        boolean found = false;        for (int i = 0; i < array.length; i++) {            if (array[i] == anEntry) {                found = true;                break;            }        }        return found;    }    @Override    public E[] toArray() {        E[] newArray = (E[]) new Object[getCurrentSize()];        int index = 0;        for (int i = 0; i < array.length; i++) {            if (array[i] != null) {                newArray[index] = array[i];                index++;            }        }        return newArray;    }}